list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX vivo: <http://vivoweb.org/ontology/core#> PREFIX event: <http://purl.org/NET/c4dm/event.owl#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX gesah: <http://ontology.tib.eu/gesah/> PREFIX obo: <http://purl.obolibrary.org/obo/> SELECT DISTINCT ?object ?barcode ?fileNum ?cultObjectLabel ?typeLabel ?curNumber WHERE { ?subject ?property ?object . ?object a gesah:Cultural_Object . ?object rdfs:label ?cultObjectLabel . ?object gesah:has_main_image ?digRep . ?digRep gesah:barcode ?barcode . ?digRep gesah:image_file_number ?fileNum . OPTIONAL{ ?subject ?property ?object . ?object a gesah:Cultural_Object . ?object vitro:mostSpecificType ?type . ?type rdfs:label ?typeLabel . } OPTIONAL { ?subject ?property ?object . ?object a gesah:Cultural_Object . ?object gesah:current_inventory_number ?curNumber . }
OPTIONAL { ?subject ?property ?object . ?object a ?subclass . } FILTER ( REPLACE(STR(?subclass),"^(.*)(#)(.*)$", "$1$2") != "http://vitro.mannlib.cornell.edu/ns/vitro/0.7#" )
} ORDER BY ?subclass ASC( ?cultObjectLabel )
