PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX vivo: <http://vivoweb.org/ontology/core#> PREFIX event: <http://purl.org/NET/c4dm/event.owl#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX gesah: <http://ontology.tib.eu/gesah/> PREFIX obo: <http://purl.obolibrary.org/obo/> SELECT DISTINCT ?label ?object ?barcode ?fileNum ?cultObjectLabel ?cultObject ?curNumber ?subclass ?typeLabel (GROUP_CONCAT(?role_label;SEPARATOR=", ") AS ?roles) WHERE { ?subject ?property ?object . ?object gesah:realized_in ?activity . ?activity gesah:has_production_output ?cultObject . ?cultObject gesah:has_main_image ?digRep . ?cultObject rdfs:label ?cultObjectLabel . ?digRep gesah:barcode ?barcode . ?digRep gesah:image_file_number ?fileNum . OPTIONAL { ?subject ?property ?object . ?object gesah:realized_in ?cultObject . ?cultObject gesah:current_inventory_number ?curNumber . } OPTIONAL { ?subject ?property ?object . ?object rdfs:label ?label . } OPTIONAL{ ?subject ?property ?object . ?object gesah:realized_in ?cultObject . ?cultObject vitro:mostSpecificType ?type . ?type rdfs:label ?typeLabel . } OPTIONAL{ ?object gesah:has_role_type ?role_type . ?role_type rdfs:label ?role_label . }
OPTIONAL { ?subject ?property ?object . ?object a ?subclass . } FILTER ( REPLACE(STR(?subclass),"^(.*)(#)(.*)$", "$1$2") != "http://vitro.mannlib.cornell.edu/ns/vitro/0.7#" )
} GROUP BY ?object ?label ?barcode ?fileNum ?cultObjectLabel ?cultObject ?curNumber ?subclass ?typeLabel ORDER BY ?subclass ASC( ?label )
