list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX gesah: <http://ontology.tib.eu/gesah/> PREFIX obo: <http://purl.obolibrary.org/obo/>
SELECT ?objEdition ?person ?personName ?place ?placeLabel ?litteralDtAppel ?dateTimeStart ?dateTimeEnd (group_concat(DISTINCT ?roleTypeLabel ; separator=", ") AS ?roleTypeLabels)
WHERE { ?subject ?property ?objEdition . ?objEdition a gesah:Edition .
OPTIONAL { ?subject ?property ?objEdition . ?objEdition a gesah:Edition . ?objEdition gesah:realizes ?role . ?role gesah:is_role_of ?person . ?role a obo:BFO_0000023 . ?role gesah:has_role_type ?roleType . ?roleType rdfs:label ?roleTypeLabel . ?person a foaf:Person. ?person rdfs:label ?personName . }
OPTIONAL { ?subject ?property ?objEdition . ?objEdition a gesah:Edition . ?objEdition gesah:has_place ?place . ?place a core:GeographicLocation . ?place rdfs:label ?placeLabel . }
OPTIONAL { ?subject ?property ?objEdition . ?objEdition a gesah:Edition . ?objEdition gesah:literal_date_appellation ?litteralDtAppel . }
OPTIONAL { ?subject ?property ?objEdition . ?objEdition a gesah:Edition . ?objEdition core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . }
OPTIONAL { ?subject ?property ?objEdition . ?objEdition a gesah:Edition . ?objEdition core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . } } GROUP BY ?objEdition ?person ?personName ?place ?placeLabel ?litteralDtAppel ?dateTimeStart ?dateTimeEnd ORDER BY ?objEdition

list-view-config.query-construct:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX gesah: <http://ontology.tib.eu/gesah/> PREFIX obo: <http://purl.obolibrary.org/obo/>
CONSTRUCT { ?subject ?property ?objEdition . ?objEdition a gesah:Edition . ?objEdition gesah:realizes ?role . ?role gesah:is_role_of ?person . ?role a obo:BFO_0000023 . ?role gesah:has_role_type ?roleType . ?roleType rdfs:label ?roleTypeLabel . ?person a foaf:Person. ?person rdfs:label ?personName . ?objEdition gesah:has_place ?place . ?place a core:GeographicLocation . ?place rdfs:label ?placeLabel . ?objEdition gesah:literal_date_appellation ?litteralDtAppel . ?objEdition core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . } WHERE { { ?subject ?property ?objEdition . ?objEdition a gesah:Edition . } UNION { ?subject ?property ?objEdition . ?objEdition a gesah:Edition . ?objEdition gesah:realizes ?role . ?role gesah:is_role_of ?person . ?role a obo:BFO_0000023 . ?role gesah:has_role_type ?roleType . ?roleType rdfs:label ?roleTypeLabel . ?person a foaf:Person. ?person rdfs:label ?personName . } UNION { ?subject ?property ?objEdition . ?objEdition a gesah:Edition . ?objEdition gesah:has_place ?place . ?place a core:GeographicLocation . ?place rdfs:label ?placeLabel . } UNION { ?subject ?property ?objEdition . ?objEdition a gesah:Edition . ?objEdition gesah:literal_date_appellation ?litteralDtAppel . } UNION { ?subject ?property ?objEdition . ?objEdition a gesah:Edition . ?objEdition core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . } UNION { ?subject ?property ?objEdition . ?objEdition a gesah:Edition . ?objEdition core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . }
}
