list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX foaf: <http://xmlns.com/foaf/0.1/> PREFIX core: <http://vivoweb.org/ontology/core#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX gesah: <http://ontology.tib.eu/gesah/> PREFIX obo: <http://purl.obolibrary.org/obo/>
SELECT ?objProduction ?person ?personName ?place ?placeLabel ?litteralDtAppel ?dateTimeStart ?dateTimeEnd (group_concat(DISTINCT ?roleTypeLabel ; separator=", ") AS ?roleTypeLabels) (group_concat(DISTINCT ?attrTypeLabel ; separator=", ") AS ?attrTypeLabels) (group_concat(DISTINCT ?materialLabel ; separator=", ") AS ?materialLabels) (group_concat(DISTINCT ?techniqueLabel ; separator=", ") AS ?techniqueLabels) (group_concat(DISTINCT ?description ; separator=", ") AS ?descriptions)
WHERE { ?subject ?property ?objProduction . ?objProduction a gesah:Production .
OPTIONAL { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction gesah:realizes ?role . ?objProduction gesah:has_participant ?person . ?role gesah:is_role_of ?person . ?role a obo:BFO_0000023 . ?role gesah:has_role_type ?roleType . ?roleType rdfs:label ?roleTypeLabel . ?person a foaf:Person. ?person rdfs:label ?personName . }
OPTIONAL { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction gesah:has_place ?place . ?place a core:GeographicLocation . ?place rdfs:label ?placeLabel . }
OPTIONAL { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction gesah:literal_date_appellation ?litteralDtAppel . }
OPTIONAL { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . }
OPTIONAL { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . }
OPTIONAL { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction gesah:has_type_of_attribution ?attrType . ?attrType a gesah:Attribution_Type . ?attrType rdfs:label ?attrTypeLabel . }
OPTIONAL { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction gesah:has_material ?material . ?material a gesah:Material . ?material rdfs:label ?materialLabel . }
OPTIONAL { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction gesah:uses_technique ?technique . ?technique a gesah:Technique . ?technique rdfs:label ?techniqueLabel . }
OPTIONAL { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction gesah:description ?description . } } GROUP BY ?objProduction ?person ?personName ?place ?placeLabel ?litteralDtAppel ?dateTimeStart ?dateTimeEnd ORDER BY ?objProduction

