CONSTRUCT { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction gesah:realizes ?role . ?objProduction gesah:has_participant ?person . ?role gesah:is_role_of ?person . ?role a obo:BFO_0000023 . ?role gesah:has_role_type ?roleType . ?roleType rdfs:label ?roleTypeLabel . ?person a foaf:Person. ?person rdfs:label ?personName . ?objProduction gesah:has_place ?place . ?place a core:GeographicLocation . ?place rdfs:label ?placeLabel . ?objProduction gesah:literal_date_appellation ?litteralDtAppel . ?objProduction core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . ?objProduction gesah:has_type_of_attribution ?attrType . ?attrType a gesah:Attribution_Type . ?attrType rdfs:label ?attrTypeLabel . ?objProduction gesah:has_material ?material . ?material a gesah:Material . ?material rdfs:label ?materialLabel . ?objProduction gesah:uses_technique ?technique . ?technique a gesah:Technique . ?technique rdfs:label ?techniqueLabel . ?objProduction gesah:description ?description . } WHERE { { ?subject ?property ?objProduction . ?objProduction a gesah:Production . } UNION { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction gesah:realizes ?role . ?objProduction gesah:has_participant ?person . ?role gesah:is_role_of ?person . ?role a obo:BFO_0000023 . ?role gesah:has_role_type ?roleType . ?roleType rdfs:label ?roleTypeLabel . ?person a foaf:Person. ?person rdfs:label ?personName . } UNION { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction gesah:has_place ?place . ?place a core:GeographicLocation . ?place rdfs:label ?placeLabel . } UNION { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction gesah:literal_date_appellation ?litteralDtAppel . } UNION { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:start ?dateTimeStartValue . ?dateTimeStartValue core:dateTime ?dateTimeStart . } UNION { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction core:dateTimeInterval ?dateTimeInterval . ?dateTimeInterval core:end ?dateTimeEndValue . ?dateTimeEndValue core:dateTime ?dateTimeEnd . } UNION { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction gesah:has_type_of_attribution ?attrType . ?attrType a gesah:Attribution_Type . ?attrType rdfs:label ?attrTypeLabel . } UNION { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction gesah:has_material ?material . ?material a gesah:Material . ?material rdfs:label ?materialLabel . } UNION { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction gesah:uses_technique ?technique . ?technique a gesah:Technique . ?technique rdfs:label ?techniqueLabel . } UNION { ?subject ?property ?objProduction . ?objProduction a gesah:Production . ?objProduction gesah:description ?description . }
}
