list-view-config.query-select:
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#> PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#> PREFIX gesah: <http://ontology.tib.eu/gesah/> SELECT ?subclass ?object ?label ?localName ?indexNo ?indexNoUrl ?indexLabel ?index WHERE { ?subject ?property ?object . LET (?localName := REPLACE(STR(?object),"^.*(#)(.*)$", "$2"))
OPTIONAL { ?subject ?property ?object . ?object rdfs:label ?label . } OPTIONAL { ?subject ?property ?object . ?object gesah:is_listed_in ?index . ?index rdfs:label ?indexLabel . } OPTIONAL { ?subject ?property ?object . ?object gesah:index_number_URL ?indexNoUrl . } OPTIONAL { ?subject ?property ?object . ?indexEntry gesah:index_number ?indexNo . }
OPTIONAL { ?subject ?property ?object . ?object a ?subclass . # Require the subclasses retrieved to be in a classgroup, since others are not generally # for display. See vivo-dev-all thread titled "Internal Entity and mostSpecificType," # Aug 9-10, 2011. # ?subclass vitro:inClassGroup ?classgroup } FILTER ( REPLACE(STR(?subclass),"^(.*)(#)(.*)$", "$1$2") != "http://vitro.mannlib.cornell.edu/ns/vitro/0.7#" )
} ORDER BY ?subclass ASC( ?label ) ASC( ?label ) ASC( ?localName )
